  public static int firstUniqChar(String s) {
    char[] tempChar = s.toCharArray();
    int[] c = new int[256];
    for (int i = 0; i < tempChar.length; i++) {
      c[tempChar[i]] = c[tempChar[i]] + 1;
    }
    for (int i = 0; i < tempChar.length; i++) {
      if(c[tempChar[i]] == 1) {
        return i;
      }
    }
    return -1;
  }
